mapper.select:

select * 
from (select rownum as rnum, BOARD_NUM, BOARD_WRITER, MEMBER_ID, PETSITTER_ID, BOARD_SUBJECT, BOARD_CONTENT, BOARD_READCOUNT, BOARD_DATE 
			from communication_board 
			where MEMBER_ID=#{MEMBER_ID} and PETSITTER_ID=#{PETSITTER_ID}
			order by BOARD_NUM desc)
where rnum >= #{startrow} and rnum <= #{endrow}
