mapper.select:

select MEMBER_NUM, MEMBER_NAME, MEMBER_SUBJECT, MEMBER_CONTENT, MEMBER_ORG_FILE, MEMBER_UP_FILE, MEMBER_READCOUNT, MEMBER_DATE, MEMBER_LIKECOUNT, MEMBER_SECRET
 	from (select rownum rnum, MEMBER_NUM, MEMBER_NAME, MEMBER_SUBJECT, MEMBER_CONTENT, MEMBER_ORG_FILE, MEMBER_UP_FILE, MEMBER_READCOUNT, MEMBER_DATE, MEMBER_LIKECOUNT, MEMBER_SECRET
 		from (select * from member_board order by MEMBER_NUM desc))
 			where rnum >= #{startrow} and rnum <= #{endrow}
